mapper.select:
SELECT M.*,R.REPORT_ISWARNING FROM MEMBER M LEFT OUTER JOIN REPORT R ON M.USID=R.REPORT_TARGET_USID 
WHERE 1=1 AND MEM_CLASS LIKE ('일반유저')

	AND ${type} LIKE '%'||#{keyword, jdbcType=VARCHAR}||'%'



	AND GENDER = #{gender, jdbcType=VARCHAR}




	AND MEM_CLASS IN
	 #{v, jdbcType=VARCHAR}




	AND LEAVE_MEM IN
	 #{a, jdbcType=INTEGER}

SELECT * FROM MEMBER WHERE 1=1 AND MEM_CLASS LIKE('전문가')


	AND ${type} LIKE '%'||#{keyword, jdbcType=VARCHAR}||'%'



	AND GENDER = #{gender, jdbcType=VARCHAR}



	AND MEM_CLASS IN
	 #{v, jdbcType=VARCHAR}




	AND LEAVE_MEM IN
	 #{a, jdbcType=INTEGER}




	ORDER BY ${type2} ASC



	ORDER BY ${type2} DESC

mapper.insert:
UPDATE NOTICE SET NOTICE_TITLE=#{notice_title},
								NOTICE_CONTENT=#{notice_content},
								NOTICE_ID=#{notice_id},
								NOTICE_ADMIN_NICKNAME=#{notice_admin_nickname}
WHERE NOTICE_ID=#{notice_id}
