mapper.select:
SELECT M.*,R.REPORT_ISWARNING FROM MEMBER M LEFT OUTER JOIN REPORT R ON M.USID=R.REPORT_TARGET_USID 
WHERE 1=1 AND MEM_CLASS LIKE ('일반유저') 

	AND ${type} LIKE '%'||#{keyword, jdbcType=VARCHAR}||'%'



	AND GENDER = #{gender, jdbcType=VARCHAR}



	AND MEM_CLASS IN
	 #{v, jdbcType=VARCHAR}




	AND LEAVE_MEM IN
	 #{a, jdbcType=INTEGER}

SELECT * FROM MEMBER WHERE 1=1 AND MEM_CLASS LIKE('전문가') 


	AND ${type} LIKE '%'||#{keyword, jdbcType=VARCHAR}||'%'



	AND GENDER = #{gender, jdbcType=VARCHAR}



	AND MEM_CLASS IN
	 #{v, jdbcType=VARCHAR}




	AND LEAVE_MEM IN
	 #{a, jdbcType=INTEGER}




	ORDER BY ${type2} ASC



	ORDER BY ${type2} DESC

mapper.update:
UPDATE NOTICE SET NOTICE_TITLE=#{noticeTitle},
								NOTICE_CONTENT=#{noticeContent},
								NOTICE_ID=#{noticeId},
								NOTICE_ADMIN_NICKNAME=#{noticeAdminNickname}
WHERE NOTICE_ID=#{noticeId}
